import React from "react";
import "react-responsive-carousel/lib/styles/carousel.min.css";
import { Carousel } from "react-responsive-carousel";
import "./Carousel.css";

const Slider = () => {
  const languageData = [
    {
      id: 1,
      title: "HTML5",
      image: require='src/asset/images/Languages/HTML5.png',
      info: "HTML5",
    },
    {
      id: 2,
      title: "CSS3",
      image: "",
      info: "CSS3",
    },
    {
      id: 3,
      title: "REACT",
      image: "",
      info: "REACT",
    },
    {
      id: 4,
      title: "BOOTSTRAP",
      image: "",
      info: "BOOTSTRAP",
    },
    {
      id: 5,
      title: "SASS",
      image: "",
      info: "SASS",
    },
    {
      id: 6,
      title: "JAVASCRIPT",
      image: "",
      info: "JAVASCRIPT",
    },
    {
      id: 7,
      title: "PHP",
      image: "",
      info: "PHP",
    },
    {
      id: 8,
      title: "MYSQL",
      image: "",
      info: "MYSQL",
    },
    {
      id: 9,
      title: "WORDPRESS",
      image: "",
      info: "WORDPRESS",
    },
    {
      id: 10,
      title: "GIT",
      image: "",
      info: "GIT",
    },
  ];

  return (
    <Carousel autoPlay interval={3000} infiniteLoop="true" showStatus={false} showThumbs={false} showArrows={false}>
      {languageData.map((slide) => (
        <div key={slide.id}>
          <img className="logoLanguage" src={slide.image} alt="" />
        </div>
      ))}
    </Carousel>
  );
};

export default Slider;
